   202→ * @param easingFn - Easing function (use easing tokens)
   203→ * @returns CSS transition string
   204→ */
   205→export function createTransition(
   206→  properties: string | string[],
   207→  durationMs: number = duration.normal,
   208→  easingFn: string = easing.standard
   209→): string {
   210→  const props = Array.isArray(properties) ? properties : [properties];
   211→  return props
   212→    .map(prop => `${prop} ${durationMs}ms ${easingFn}`)
   213→    .join(', ');
   214→}
