   229→  iterationCount: number | 'infinite' = 1,
   230→  fillMode: 'none' | 'forwards' | 'backwards' | 'both' = 'both'
   231→): string {
   232→  return `${animationName} ${durationMs}ms ${easingFn} ${iterationCount} ${fillMode}`;
