     5→
     6→export interface ButtonProps extends Omit<MuiButtonProps, 'variant'> {
     7→  variant?: 'primary' | 'secondary' | 'tertiary' | 'danger' | 'success'
     8→  loading?: boolean
     9→  success?: boolean
    10→  icon?: React.ReactNode
    11→  iconPosition?: 'start' | 'end'
    12→}
    13→
    14→const StyledButton = styled(MuiButton)(() => ({
    15→  fontWeight: 600,
    16→  transition: 'all 200ms ease-out',
    17→  '&:hover': {
    18→    transform: 'translateY(-1px)',
    19→  },
    20→  '&:active': {
    21→    transform: 'scale(0.98)',
    22→  },
    23→  '&.Mui-disabled': {
    24→    transform: 'none',
    25→  },
    26→}))
    27→
    28→const AnimatedCheckIcon = styled(CheckCircleIcon)(() => ({
    29→  animation: `${scaleIn} ${duration.normal}ms ${easing.decelerate}`,
    30→  '@keyframes draw-circle': {
    31→    from: {
    32→      strokeDashoffset: 1,
    33→    },
    34→    to: {
    35→      strokeDashoffset: 0,
    36→    },
    37→  },
    38→  '& path': {
    39→    strokeDasharray: '1',
    40→    strokeDashoffset: '0',
    41→    animation: `draw-circle ${duration.slow}ms ${easing.decelerate}`,
    42→  },
    43→}))
    44→
    45→export function Button({
    46→  variant = 'primary',
    47→  loading = false,
    48→  success = false,
    49→  icon,
    50→  iconPosition = 'start',
    51→  children,
    52→  disabled,
    53→  ...props
    54→}: ButtonProps) {
    55→  const getMuiVariant = (): MuiButtonProps['variant'] => {
    56→    switch (variant) {
    57→      case 'primary':
    58→      case 'danger':
    59→      case 'success':
    60→        return 'contained'
    61→      case 'secondary':
    62→        return 'outlined'
    63→      case 'tertiary':
    64→        return 'text'
    65→      default:
    66→        return 'contained'
    67→    }
    68→  }
    69→
    70→  const getColor = (): MuiButtonProps['color'] => {
    71→    switch (variant) {
    72→      case 'primary':
    73→        return 'primary'
    74→      case 'secondary':
    75→        return 'primary'
    76→      case 'tertiary':
    77→        return 'primary'
    78→      case 'danger':
    79→        return 'error'
    80→      case 'success':
    81→        return 'success'
    82→      default:
    83→        return 'primary'
    84→    }
    85→  }
    86→
    87→  // Determine which icon to show based on state priority: success > loading > custom icon
    88→  const getStartIcon = () => {
    89→    if (success && iconPosition === 'start') {
    90→      return <AnimatedCheckIcon fontSize="small" />
    91→    }
    92→    if (loading) {
    93→      return <CircularProgress size={18} color="inherit" />
    94→    }
    95→    if (icon && iconPosition === 'start') {
    96→      return icon
    97→    }
    98→    return undefined
    99→  }
   100→
   101→  const getEndIcon = () => {
   102→    if (success && iconPosition === 'end') {
   103→      return <AnimatedCheckIcon fontSize="small" />
   104→    }
   105→    if (icon && iconPosition === 'end') {
   106→      return icon
   107→    }
   108→    return undefined
   109→  }
   110→
   111→  return (
   112→    <StyledButton
   113→      variant={getMuiVariant()}
   114→      color={getColor()}
   115→      disabled={disabled || loading}
   116→      startIcon={getStartIcon()}
   117→      endIcon={getEndIcon()}
   118→      {...props}
   119→    >
   120→      {children}
   121→    </StyledButton>
   122→  )
   123→}
