   136→      sx={{
   137→        minWidth: 'auto',
   138→        p: 1,
   139→        ...props.sx,
   140→      }}
   141→      {...props}
   142→    >
   143→      {loading ? <CircularProgress size={20} color="inherit" /> : children}
   144→    </StyledButton>
   145→  )
   146→}
