    45→function getInitials(name: string): string {
    46→  return name
    47→    .split(' ')
    48→    .map((n) => n[0])
    49→    .join('')
    50→    .toUpperCase()
    51→    .slice(0, 2)
    52→}
    53→
    54→function stringToColor(string: string): string {
    55→  let hash = 0
    56→  for (let i = 0; i < string.length; i++) {
    57→    hash = string.charCodeAt(i) + ((hash << 5) - hash)
    58→  }
    59→  const colors = ['#0369A1', '#0D9488', '#7C3AED', '#DB2777', '#EA580C', '#16A34A']
    60→  return colors[Math.abs(hash) % colors.length]
    61→}
    62→
    63→export function Avatar({
    64→  name,
    65→  src,
    66→  size = 'medium',
    67→  variant = 'circular',
    68→  color,
