    70→}: AvatarProps) {
    71→  const avatarSize = sizeMap[size]
    72→  const bgColor = color ? undefined : name ? stringToColor(name) : undefined
    73→
    74→  const avatar = (
    75→    <StyledAvatar
    76→      src={src}
    77→      variant={variant}
    78→      avatarSize={avatarSize}
    79→      avatarColor={bgColor}
    80→      sx={{
    81→        fontSize: fontSizeMap[size],
    82→        ...(color && { bgcolor: `${color}.main` }),
    83→      }}
    84→    >
    85→      {!src && name && getInitials(name)}
    86→    </StyledAvatar>
    87→  )
