     8→export function ThemeToggle() {
     9→  const { mode, setMode, isDark } = useThemeMode()
    10→  const [anchorEl, setAnchorEl] = useState<null | HTMLElement>(null)
    11→  const open = Boolean(anchorEl)
    12→
    13→  const handleClick = (event: React.MouseEvent<HTMLElement>) => {
    14→    setAnchorEl(event.currentTarget)
    15→  }
    16→
    17→  const handleClose = () => {
    18→    setAnchorEl(null)
    19→  }
    20→
    21→  const handleSelect = (newMode: 'light' | 'dark' | 'system') => {
    22→    setMode(newMode)
    23→    handleClose()
    24→  }
    25→
    26→  const getCurrentIcon = () => {
    27→    if (mode === 'system') return <SettingsBrightnessIcon />
    28→    return isDark ? <DarkModeIcon /> : <LightModeIcon />
    29→  }
    30→
    31→  return (
    32→    <>
    33→      <Tooltip title="Theme">
    34→        <IconButton
    35→          onClick={handleClick}
    36→          size="small"
    37→          sx={{
    38→            color: 'text.secondary',
    39→            '&:hover': { color: 'text.primary' },
    40→          }}
    41→        >
    42→          {getCurrentIcon()}
    43→        </IconButton>
    44→      </Tooltip>
    45→      <Menu
    46→        anchorEl={anchorEl}
    47→        open={open}
    48→        onClose={handleClose}
