echo "  - Navigate to: http://localhost:3000/projects/1/timeline"
echo "  - Design Reference: ./design-assets/project/18-gantt-timeline.png"
echo ""
echo "Process IDs:"
echo "  Frontend: $FRONTEND_PID"
echo ""
echo "To stop all services:"
echo "  kill $FRONTEND_PID"
echo ""
