     1→import { useEffect } from 'react';
     2→import { useTranslation } from 'react-i18next';
     3→
     4→export const useLanguage = () => {
     5→  const { i18n } = useTranslation();
     6→
     7→  const currentLanguage = i18n.language;
     8→  const isRTL = currentLanguage === 'he';
     9→
    10→  useEffect(() => {
    11→    document.documentElement.dir = isRTL ? 'rtl' : 'ltr';
    12→    document.documentElement.lang = currentLanguage;
    13→  }, [currentLanguage, isRTL]);
    14→
    15→  const changeLanguage = (lang: 'en' | 'he') => {
    16→    i18n.changeLanguage(lang);
    17→  };
    18→
    19→  return {
    20→    currentLanguage,
    21→    isRTL,
    22→    changeLanguage,
    23→  };
    24→};
