    59→    setSelectedProjectId(newProjectId)
    60→    navigate(`/projects/${newProjectId}`)
    61→  }
    62→
    63→  const handleLogout = () => {
    64→    localStorage.removeItem('authToken')
    65→    localStorage.removeItem('userId')
    66→    navigate('/login')
    67→  }
    68→
    69→  const handleMobileDrawerToggle = () => {
    70→    setMobileDrawerOpen(!mobileDrawerOpen)
    71→  }
    72→
    73→  const handleMobileDrawerClose = () => {
    74→    setMobileDrawerOpen(false)
    75→  }
    76→
    77→  // Close drawer when navigating on mobile
    78→  useEffect(() => {
    79→    if (!isDesktop) {
    80→      handleMobileDrawerClose()
    81→    }
    82→  }, [location.pathname, isDesktop])
    83→
    84→  // Close drawer when resizing to desktop
    85→  useEffect(() => {
    86→    if (isDesktop) {
    87→      handleMobileDrawerClose()
    88→    }
    89→  }, [isDesktop])
    90→
    91→  if (loading || !currentUser) {
    92→    return (
    93→      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', minHeight: '100vh' }}>
    94→        <CircularProgress />
    95→      </Box>
    96→    )
    97→  }
    98→
    99→  return (
   100→    <Box sx={{ display: 'flex', minHeight: '100vh' }}>
   101→      <Header
   102→        user={currentUser}
   103→        currentProject={currentProject}
   104→        projects={projects}
   105→        onProjectChange={handleProjectChange}
   106→        onLogout={handleLogout}
   107→        onMobileMenuClick={handleMobileDrawerToggle}
   108→      />
   109→      <Sidebar
   110→        projectId={selectedProjectId}
   111→        mobileOpen={mobileDrawerOpen}
   112→        onMobileClose={handleMobileDrawerClose}
   113→      />
   114→      <Box
   115→        component="main"
   116→        sx={{
   117→          flexGrow: 1,
   118→          p: 3,
   119→          ml: `${DRAWER_WIDTH}px`,
   120→          bgcolor: 'background.default',
   121→          minHeight: '100vh',
   122→        }}
   123→      >
   124→        <Toolbar />
   125→        <Outlet context={{ projectId: selectedProjectId, project: currentProject }} />
   126→      </Box>
   127→    </Box>
   128→  )
   129→}
