     4→interface AvatarProps {
     5→  name?: string
     6→  src?: string
     7→  size?: 'small' | 'medium' | 'large' | 'xlarge'
     8→  variant?: 'circular' | 'rounded' | 'square'
     9→  color?: 'primary' | 'secondary' | 'success' | 'warning' | 'error' | 'info'
    10→  showTooltip?: boolean
