    25→}
    26→
    27→const StyledAvatar = styled(MuiAvatar, {
    28→  shouldForwardProp: (prop) => prop !== 'avatarSize' && prop !== 'avatarColor',
    29→})<{ avatarSize: number; avatarColor?: string }>(({ theme, avatarSize, avatarColor }) => ({
    30→  width: avatarSize,
    31→  height: avatarSize,
    32→  backgroundColor: avatarColor || theme.palette.primary.main,
    33→  color: theme.palette.primary.contrastText,
    34→  fontWeight: 600,
    35→  transition: 'transform 150ms ease-out',
    36→  '&:hover': {
    37→    transform: 'scale(1.05)',
    38→  },
    39→}))
    40→
    41→function getInitials(name: string): string {
    42→  return name
    43→    .split(' ')
    44→    .map((n) => n[0])
    45→    .join('')
    46→    .toUpperCase()
    47→    .slice(0, 2)
    48→}
    49→
    50→function stringToColor(string: string): string {
    51→  let hash = 0
    52→  for (let i = 0; i < string.length; i++) {
    53→    hash = string.charCodeAt(i) + ((hash << 5) - hash)
    54→  }
    55→  const colors = ['#0369A1', '#0D9488', '#7C3AED', '#DB2777', '#EA580C', '#16A34A']
    56→  return colors[Math.abs(hash) % colors.length]
    57→}
    58→
    59→export function Avatar({
    60→  name,
    61→  src,
    62→  size = 'medium',
    63→  variant = 'circular',
    64→  color,
    65→  showTooltip = false,
    66→}: AvatarProps) {
    67→  const avatarSize = sizeMap[size]
    68→  const bgColor = color ? undefined : name ? stringToColor(name) : undefined
    69→
    70→  const avatar = (
    71→    <StyledAvatar
    72→      src={src}
    73→      variant={variant}
    74→      avatarSize={avatarSize}
    75→      avatarColor={bgColor}
    76→      sx={{
    77→        fontSize: fontSizeMap[size],
    78→        ...(color && { bgcolor: `${color}.main` }),
    79→      }}
    80→    >
    81→      {!src && name && getInitials(name)}
    82→    </StyledAvatar>
    83→  )
    84→
    85→  if (showTooltip && name) {
    86→    return <Tooltip title={name}>{avatar}</Tooltip>
    87→  }
    88→
    89→  return avatar
    90→}
