     1â†’import {
     2â†’  FormControl,
     3â†’  InputLabel,
     4â†’  Select as MuiSelect,
     5â†’  SelectProps as MuiSelectProps,
     6â†’  MenuItem,
     7â†’  FormHelperText,
     8â†’} from '@mui/material'
     9â†’import { styled } from '@mui/material/styles'
    10â†’import { SelectInputBaseProps, SelectOption } from './types'
    11â†’
    12â†’export interface SelectInputProps<T = string> extends SelectInputBaseProps<T> {
    13â†’  /**
    14â†’   * Additional Material UI Select props
    15â†’   */
    16â†’  SelectProps?: Omit<MuiSelectProps<T | T[]>, 'value' | 'onChange' | 'error' | 'label' | 'required' | 'disabled' | 'multiple'>
    17â†’}
    18â†’
    19â†’const StyledFormControl = styled(FormControl)(({ theme }) => ({
    20â†’  '& .MuiOutlinedInput-root': {
    21â†’    borderRadius: 8,
    22â†’    transition: 'all 200ms ease-out',
    23â†’    '&:hover': {
    24â†’      '& .MuiOutlinedInput-notchedOutline': {
    25â†’        borderColor: theme.palette.primary.main,
    26â†’      },
    27â†’    },
    28â†’    '&.Mui-focused': {
    29â†’      '& .MuiOutlinedInput-notchedOutline': {
    30â†’        borderWidth: 2,
    31â†’      },
    32â†’    },
    33â†’  },
    34â†’  '& .MuiInputLabel-root': {
    35â†’    fontSize: '0.875rem',
    36â†’  },
    37â†’  '& .MuiSelect-select': {
    38â†’    fontSize: '0.875rem',
    39â†’  },
    40â†’}))
    41â†’
    42â†’/**
    43â†’ * SelectInput component for dropdown selections
    44â†’ *
    45â†’ * Wraps Material UI Select with custom styling and simplified onChange API.
    46â†’ * Supports both single and multiple selection modes.
    47â†’ */
    48â†’export function SelectInput<T = string>({
    49â†’  label,
    50â†’  value,
    51â†’  onChange,
    52â†’  options,
    53â†’  error,
    54â†’  helperText,
    55â†’  required = false,
    56â†’  disabled = false,
    57â†’  multiple = false,
    58â†’  id,
    59â†’  name,
    60â†’  className,
    61â†’  SelectProps,
    62â†’}: SelectInputProps<T>) {
    63â†’  const handleChange = (event: any) => {
    64â†’    onChange(event.target.value as T | T[])
    65â†’  }
    66â†’
    67â†’  return (
    68â†’    <StyledFormControl
    69â†’      fullWidth
    70â†’      error={!!error}
    71â†’      required={required}
    72â†’      disabled={disabled}
    73â†’      className={className}
    74â†’      variant="outlined"
    75â†’    >
    76â†’      {label && <InputLabel id={id ? `${id}-label` : undefined}>{label}</InputLabel>}
    77â†’      <MuiSelect
    78â†’        id={id}
    79â†’        name={name}
    80â†’        labelId={id ? `${id}-label` : undefined}
    81â†’        label={label}
    82â†’        value={value}
    83â†’        onChange={handleChange}
    84â†’        multiple={multiple}
    85â†’        {...SelectProps}
    86â†’      >
    87â†’        {options.map((option) => (
    88â†’          <MenuItem key={String(option.value)} value={option.value as any} disabled={option.disabled}>
    89â†’            {option.label}
    90â†’          </MenuItem>
    91â†’        ))}
    92â†’      </MuiSelect>
    93â†’      {(error || helperText) && <FormHelperText>{error || helperText}</FormHelperText>}
    94â†’    </StyledFormControl>
    95â†’  )
    96â†’}
