./frontend/src/components/ui/index.ts:export { Card, GlassCardComponent, KPICard, FeatureCard, ProjectCard, CardContent, CardHeader, CardActions } from './Card'

interface ProjectCardProps {
  name: string
  code?: string
  progress: number
  status: 'active' | 'on_hold' | 'completed' | 'archived'
  imageUrl?: string
  onClick?: () => void
}

export function ProjectCard({ name, code, progress, status, imageUrl, onClick }: ProjectCardProps) {
  const getStatusColor = () => {
    switch (status) {
      case 'active': return 'success'
      case 'on_hold': return 'warning'
      case 'completed': return 'info'
      case 'archived': return 'default'
      default: return 'default'
    }
  }

  return (
    <StyledCard hoverable onClick={onClick}>
      {imageUrl && (
        <Box
          sx={{
            height: 140,
            backgroundImage: `url(${imageUrl})`,
            backgroundSize: 'cover',
            backgroundPosition: 'center',
          }}
        />
      )}
      <CardContent sx={{ p: 2 }}>
        <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', mb: 1 }}>
          <Typography variant="subtitle1" sx={{ fontWeight: 600 }}>
            {name}
          </Typography>
          <Box
            sx={{
              px: 1,
              py: 0.25,
              borderRadius: 1,
              bgcolor: `${getStatusColor()}.main`,
              color: 'white',
              fontSize: '0.65rem',
              fontWeight: 600,
              textTransform: 'uppercase',
            }}
          >
            {status.replace('_', ' ')}
          </Box>
        </Box>
        {code && (
          <Typography variant="caption" color="text.secondary" sx={{ display: 'block', mb: 1.5 }}>
            {code}
          </Typography>
        )}
        <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
          <Box
            sx={{
              flex: 1,
              height: 6,
              borderRadius: 3,
              bgcolor: 'action.hover',
              overflow: 'hidden',
            }}
          >
            <Box
              sx={{
                width: `${progress}%`,
                height: '100%',
                borderRadius: 3,
                bgcolor: 'primary.main',
                transition: 'width 300ms ease-out',
              }}
            />
          </Box>
          <Typography variant="caption" sx={{ fontWeight: 600, color: 'text.secondary' }}>
            {progress}%
          </Typography>
        </Box>
      </CardContent>
    </StyledCard>
  )
}
